import { uiActions } from "./ui-slice";
import { cartActions } from "./cart-slice";

export const fetchCartData = () => {
  return async(dispatch) => {
    const fetchData = async () => {
      const response = await fetch(
        "https://react-http-18358-default-rtdb.firebaseio.com/cart.json"
      );
      if (!response.ok) {
        throw new Error("could not fetch cart Data!.");
      }
      const data = await response.json();
      return data;
    };
    try{
     const cartData = await fetchData();
     dispatch(cartActions.replaceCart(cartData))
    } catch(error){
        dispatch(
            uiActions.showNotification({
              status: "error",
              title: "Error",
              message: "Fetch cart data failed",
            })
          );

    }
  };
};

export const sendCartData = (cart) => {
  return async (dispatch) => {
    dispatch(
      uiActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data",
      })
    );

    const sendRequest = async () => {
      const response = await fetch(
        "https://react-http-18358-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
        //   body: JSON.stringify(cart), instead of giving whole cart we only pass speciic data
        body :JSON.stringify({items:cart.items, totalQuantity})
        }
      );
      if (!response.ok) {
        throw new Error("Sending cart data failed.");
      }
    };

    try {
      await sendRequest();
      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Success",
          message: "Send cart data successfully",
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error",
          message: "Sending cart data failed",
        })
      );
    }
  };
};
